from org.apache.commons.io import IOUtils
from java.nio.charset import StandardCharsets
from org.apache.nifi.processor.io import StreamCallback

# Define a subclass of StreamCallback for use in session.write()
class PyStreamCallback(StreamCallback):
    def __init__(self):
        pass
    def process(self, inputStream, outputStream):
        text = IOUtils.toString(inputStream, StandardCharsets.UTF_8)
        self.validation_check_flag=1
        line_list = text.split("\n")
        pass_value=''
        fail_value=''
        final_value=''
        status_list=[]
        for i in line_list:            
            my_list = i.split(",")
            if len(my_list)>1:
                if 'PASS' in my_list:
                    status_list.append('PASS')
                    pass_value+='<tr><td>'+my_list[2]+'</td><td>'+my_list[3]+'</td><td>'+my_list[4]+'</td> <td>'+my_list[5]+'</td><td>'+my_list[6]+'</td><td>'+my_list[7]+'</td><td>'+my_list[8]+'</td><td>'+my_list[9]+'</td><td bgcolor="00FF00">'+my_list[10]+'</td></tr>'
                elif 'FAIL' in my_list:
                    status_list.append('FAIL')
                    fail_value+='<tr><td>'+my_list[2]+'</td><td>'+my_list[3]+'</td><td>'+my_list[4]+'</td> <td>'+my_list[5]+'</td><td>'+my_list[6]+'</td><td>'+my_list[7]+'</td><td>'+my_list[8]+'</td><td>'+my_list[9]+'</td><td bgcolor="FF0000">'+my_list[10]+'</td></tr>'
                else:
                    header_value='<tr><td>'+my_list[2]+'</td><td>'+my_list[3]+'</td><td>'+my_list[4]+'</td> <td>'+my_list[5]+'</td><td>'+my_list[6]+'</td><td>'+my_list[7]+'</td><td>'+my_list[8]+'</td><td>'+my_list[9]+'</td><td>'+my_list[10]+'</td></tr>'
        final_value=header_value+pass_value+fail_value
        for status in status_list:
            if 'FAIL'==status:
                self.validation_check_flag=0
            elif 'PASS'==status and self.validation_check_flag==1:
                self.validation_check_flag=1
            else:
                pass
        if self.validation_check_flag==1:
            outputStream.write(bytearray('Validation Check Passed'.encode('utf-8')))
        else:
            outputStream.write(bytearray('Validation Check Failed'.encode('utf-8')))
        main_html_body='<html><body><table border="1"><thead><tr style="text-align: left;"></tr></thead><tbody>'+final_value+'</tbody></table></body></html>'
        outputStream.write(bytearray(main_html_body.encode('utf-8')))

# end class
flowFile = session.get()
if(flowFile != None):
    PyStreamCallbackObj = PyStreamCallback()
    flowFile = session.write(flowFile, PyStreamCallbackObj)
if(PyStreamCallbackObj.validation_check_flag==1):
    session.putAttribute(flowFile,"MESSAGE","validation check passed")
else:
    session.putAttribute(flowFile,"MESSAGE","validation check failed")
session.transfer(flowFile, REL_SUCCESS)
session.commit()
